mapper.select:

SELECT a.schedule_num AS schedule_Num, a.member_Id, a.city_Num, a.start_Num, a.end_Date, a.reg_Date, a.memo
FROM (
    SELECT rownum AS rnum, a.schedule_num, a.member_Id, a.city_Num, a.start_Num, a.end_Date, a.reg_Date, a.delete_Yn, a.memo
    FROM schedule a
    WHERE rownum BETWEEN #{startNo} AND #{endNo}
) a
JOIN city b ON a.city_Num = b.city_Num
WHERE a.rnum BETWEEN #{startNo} AND #{endNo}

mapper.insert:

INSERT INTO us ( 
    US_NUM, CITY_NAME, WRITER, US_CNT, START_DATE, END_DATE, TITLE , CONTENT
) VALUES ( 
    #{us_num}, #{city_name}, #{writer}, #{us_cnt},  #{start_Date}, #{end_Date}, #{title}, #{content} 
)
