mapper.select:
WITH OrderedUs AS ( SELECT a.us_num, a.DOE_NAME, a.writer, a.us_cnt, a.regdate, a.start_Date, a.end_Date, a.title, a.content, a.imageurl, a.VIEWCOUNT, ROW_NUMBER() OVER (
PARTITION BY a.DOE_NAME
ORDER BY
a.us_comment DESC
a.VIEWCOUNT DESC
a.us_num DESC
) AS rnum FROM us a
where a.DOE_NAME = #{DOE_NAME}
) SELECT u.*, uf.usfilenum as fileNo FROM OrderedUs u LEFT OUTER JOIN us_file uf ON u.us_num = uf.us_num WHERE u.rnum BETWEEN #{startNo, jdbcType=INTEGER} AND #{endNo, jdbcType=INTEGER}

mapper.update:
update us set
   viewCount = viewCount + 1 
where US_NUM = #{us_num}

UPDATE us
SET status = 'Y'
WHERE us_num = #{us_num}

UPDATE us
SET status = 'N'
WHERE us_num = #{us_num}
